(201,'CREMA DE MANTEQUILLA FRANCESA','[{\"name\":\"Yemas de Huevo\",\"weight\":\"80\"},{\"name\":\"Az\\u00facar\",\"weight\":\"125\"},{\"name\":\"Mantequilla\",\"weight\":\"125\"},{\"name\":\"Agua\",\"weight\":\"50\"}]','<p>1.- &nbsp;COLOCAR EL AZ&Uacute;CAR Y EL AGUA EN UNA CACEROLA Y LLEVAR A EBULLICI&Oacute;N HASTA LOS 110 &deg; C.</p>\r\n<p>2.- VOLCAR EN HILO SOBRE LAS YEMAS SIN DEJAR DE BATIR A ALTA VELOCIDAD HASTA QUE LA MEZCLA ENFR&Iacute;E.</p>\r\n<p>3.- &nbsp;IR AGREGANDO LA MANTEQUILLA POCO A POCO HASTA CONSEGIR UNA CREMA UNTUOSA Y HOMOG&Eacute;NIA.</p>\r\n<p>4.- &nbsp;SE LE PUEDE AGREGAR PRALINE, CHOCOLATE, CAF&Eacute; ETC. EL BATIDO DE HACE CON GLOBO.</p>\r\n<p>&nbsp;</p>','78','2024-01-04 21:25:03','2024-01-04 21:25:03'),
(202,'GANACHE DE FRAMBUESA','[{\"name\":\"Pure Frambuesa\",\"weight\":\"60\"},{\"name\":\"Az\\u00facar\",\"weight\":\"40\"},{\"name\":\"Crema de Leche\",\"weight\":\"45\"},{\"name\":\"Chocolate Blanco\",\"weight\":\"150\"},{\"name\":\"Mantequilla\",\"weight\":\"20\"}]','<p>1.- &nbsp;HERVIR UNOS SEGUNDOS EL PURE, CREMA DE LECHE&nbsp; Y AZ&Uacute;CAR.</p>\r\n<p>2.- A&Ntilde;ADIR SOBRE EL CHOCOLATE BLANCO TROCEADO&nbsp; MEZCLAR HASTA FUNDIR.</p>\r\n<p>3.- &nbsp;DEJAR ENFRIAR HASTA LOS 35 &deg; C E INCORPORAR LA MANTEQUILLA. EMULSIONAR CON MIXER UNOS SEGUNDOS.</p>\r\n<p>4.- RESERVAR.</p>','78','2024-01-04 21:25:03','2024-01-04 21:25:03'),
(203,'CONFITURA DE FRAMBUESA','[{\"name\":\"Az\\u00facar\",\"weight\":\"80\"},{\"name\":\"Pure  Frambuesa\",\"weight\":\"160\"},{\"name\":\"Pectina\",\"weight\":\"5\"}]','<p>1.- &nbsp;MEZCLAR EL AZ&Uacute;CAR CON LA PECTINA.</p>\r\n<p>2.- LLEVAR A HERVOR A 40 &deg; C&nbsp; EL PURE Y AGREGAR LA MEZCLA DE AZ&Uacute;CAR.</p>\r\n<p>3.- DEJAR HERVIR UNO SEGUNDOS Y RETIRAR DEL FUEGO. RESERVAMOS.</p>','78','2024-01-04 21:25:03','2024-01-04 21:25:03'),
(204,'GLASEADO ESPEJO FRAMBUESA  Y ARANDANOS','[{\"name\":\"Frambuesas\",\"weight\":\"150\"},{\"name\":\"Arandanos\",\"weight\":\"100\"},{\"name\":\"Glucosa\",\"weight\":\"85\"},{\"name\":\"Az\\u00facar\",\"weight\":\"100\"},{\"name\":\"Agua\",\"weight\":\"85\"},{\"name\":\"Pectina\",\"weight\":\"6\"}]','<p>1.- &nbsp;PONEMOS TODAS LAS FRUTAS A COCINAR 10 A 15 MINUTOS.&nbsp; HASTA OBTENER ALGO SILIMAR A UNA COMPOTA.</p>\r\n<p>2.- TRITURAR EN CALIENTE TODA LA FRUTA BIEN TRITURADA PARA QUE QUEDE FINO. CON MIXER.</p>\r\n<p>3.- COLAMOS PARA QUITAR TODAS LAS SEMILLAS.</p>\r\n<p>4.- MEZCLAR EL AZ&Uacute;CAR CON LA PECTINA Y RESERVAMOS.</p>\r\n<p>5.- &nbsp;LLEVAR A FUEGO EL PURE, EL AGUA Y LA GLUCOSA HASTA ALCANZAR UNOS 45 A 50 &deg; C.</p>\r\n<p>6.- A&Ntilde;ADIR LA MEZCLA DE AZUCAR Y PECTINA SIN DEJAR DE REMOVER DEJAMOS QUE HIERVA UNOS SEGUNDOS.</p>\r\n<p>7.- &nbsp;RETIRAMOS DEL FUEGO Y COLAMOS. TAPAMOS CON PAPEL FILM TOCANDO LA PREPARACI&Oacute;N.</p>\r\n<p>8.- LLEVAMOS A NEVERA 12 HORAS. AL UTILIZAR CALENTAMOS A 40 &deg; C.</p>','78','2024-01-04 21:25:03','2024-01-04 21:25:03'),
(205,'JALEA DE MANGO','[{\"name\":\"Az\\u00facar\",\"weight\":\"40\"},{\"name\":\"Pectina\",\"weight\":\"5\"},{\"name\":\"Pure de Mango\",\"weight\":\"150\"},{\"name\":\"Glucosa\",\"weight\":\"25\"},{\"name\":\"Jugo de Lim\\u00f3n\",\"weight\":\"6\"}]','<p>1.- MEZCLAR LA PECTINA CON EL AZ&Uacute;CAR. RESERVAR.</p>\r\n<p>2.- LLEVAR A HERVIR 40 &deg; C&nbsp; EL PURE DE MANGO CON LA GLUCOSA.</p>\r\n<p>3.- &nbsp;INCORPORAR LA MEZCLA DE AZ&Uacute;CAR Y PECTINA.</p>\r\n<p>4.- DEJAR HERVIR UNOS SEGUNDOS PARA ACTIVAR LA PECTINA.</p>\r\n<p>5.- APARTAMOS DEL FUEGO E INCORPORAR EL ZUMO DE LIM&Oacute;N.</p>\r\n<p>6.- VERTER EN MOLDE CUADRADO DE 12 CM RECUBIERTO CON UNA TIRA DE ACETATO.</p>\r\n<p>7.- &nbsp;LLEVAR AL CONGELADOR PARA QUE GELIFIQUE.</p>','79','2024-01-05 15:30:21','2024-01-05 15:30:21'),
