(35,'TORTA DE ALMENDRAS','202311291535TORTA DE ALMENDRAS.jpg','<p style=\"text-align: center;\"><span style=\"text-decoration: underline;\"><strong>MONTAJE TORTA DE ALMENDRAS</strong></span></p>\r\n<p style=\"text-align: left;\">1.- SE TOMA UN DISCO DE BIZCOCHO DACQUOISE Y SE HUMEDECE CON EL ALM&Iacute;BAR.</p>\r\n<p style=\"text-align: left;\">2.- SE COLOCA UNA CAPA DE CREMA DE MANTEQUILLA CON PRALIN&Eacute;.</p>\r\n<p style=\"text-align: left;\">3.- SE COLOCA ENCIMA OTRO DISCO DE BIZCOCHO DACQUOISE Y SE HUMEDECE CON ALM&Iacute;BAR.</p>\r\n<p style=\"text-align: left;\">4.- SE COLOCA UNA CAPA DE CREMA DE MANTEQUILLA CON PRALIN&Eacute;.</p>\r\n<p style=\"text-align: left;\">5.- SE COLOCA ENCIMA OTRO DISCO DE BIZCOCHO DACQUOISE Y SE HUMEDECE CON ALM&Iacute;BAR.</p>\r\n<p style=\"text-align: left;\">6.- SE CUBRE TOTALMENTE CON CREMA DE MANTEQUILLA.</p>\r\n<p style=\"text-align: left;\">7.- SE DECORA CON ALMENDRAS FILETEADAS O PRALIN&Eacute;.</p>\r\n<p style=\"text-align: left;\">8.- SE LLEVA A NEVERA Y AL SALIR SE DECORA NUEVAMENTE CON ALMENDRAS TOSTADAS.</p>\r\n<p style=\"text-align: left;\">&nbsp;</p>\r\n<p style=\"text-align: left;\">&nbsp;</p>',0,NULL,0,NULL,'2023-11-29 19:35:51','2023-11-29 19:35:51'),
(36,'MOUSSE DE PARCHITA','202312101520MOUSSE DE PARCHITA.jpg','<p style=\"text-align: center;\"><strong><span style=\"text-decoration: underline;\">MONTAJE</span></strong></p>\r\n<p style=\"text-align: left;\">1.- EN UN ARO SIN FIN COLOCAR UN DISCO DE G&Eacute;NOISE EN LA BASE, HIDRATAR CON EL ALM&Iacute;BAR.</p>\r\n<p style=\"text-align: left;\">2.- VOLCAR LA MITAD DE LA MOUSSE.</p>\r\n<p style=\"text-align: left;\">3.- COLOCAR OTRO DISCO DE G&Eacute;NOISE, HIDRATAR CON EL ALM&Iacute;BAR.</p>\r\n<p style=\"text-align: left;\">4.- VOLCAR EL RESTO DE LA MOUSSE.</p>\r\n<p style=\"text-align: left;\">5.- EMPAREJAR LA SUPERFICIE Y LLEVAR A LA NEVERA HASTA QUE LA MOUSSE EST&Eacute; FIRME.</p>\r\n<p style=\"text-align: left;\">6.- DECORAR CON EL ESPEJO DE PARCHITA Y LLEVAR A REFRIGERACI&Oacute;N.</p>',0,NULL,0,NULL,'2023-12-10 19:20:50','2023-12-10 19:20:50'),
(37,'TIRAMISÚ','202312111239TIRAMISU.jpg','<p style=\"text-align: center;\"><strong><span style=\"text-decoration: underline;\">MONTAJE</span></strong></p>\r\n<p style=\"text-align: left;\">1.- HIDRATAR LAS PLANTILLAS EN EL ALM&Iacute;BAR DE CAF&Eacute;, EL CUAL DEBE DE ESTAR FRIO.</p>\r\n<p style=\"text-align: left;\">2.- COLOCAR UNA CAPA DE PLANTILLAS EN LA BASE DEL MOLDE A UTILIZAR.</p>\r\n<p style=\"text-align: left;\">3.- COLOCAR UN TERCIO DE LA CREMA DE TIRAMIS&Uacute;.</p>\r\n<p style=\"text-align: left;\">4.- COLOCAR OTRA CAPA DE PLANTILLAS.</p>\r\n<p style=\"text-align: left;\">5.- COLOCAR OTRO TERCIO DE LA CREMA DE TIRAMIS&Uacute;.</p>\r\n<p style=\"text-align: left;\">6.- VOLVAR A COLOCAR OTRA CAPA DE PLANTILLAS Y TERMINAR CON LA CREMA DE TIRAMIS&Uacute;.</p>\r\n<p style=\"text-align: left;\">7.- ENFRIAR.</p>\r\n<p style=\"text-align: left;\">8.- ESPOLVOREAR CON CACAO AMARGO JUSTO EN EL MOMENTO DE SERVIR.</p>\r\n<p style=\"text-align: left;\"><strong><span style=\"text-decoration: underline;\">A&nbsp;SABER...</span></strong></p>\r\n<p style=\"text-align: left;\">A.- PUEDE LLEVARSE AL CONGELADOR DE UN D&Iacute;A PARA OTRO O RESERVAR HASTA 15 D&Iacute;AS BIEN TAPADO CON PAPEL FLIM.&nbsp;</p>',1,'2',0,NULL,'2023-12-11 16:39:28','2023-12-11 16:39:28'),
(38,'MERENGÓN DE GUANÁBANA','202312121210MERENGON DE GUANABANA.jpg','<p style=\"text-align: center;\"><strong><span style=\"text-decoration: underline;\">MONTAJE</span></strong></p>\r\n<p style=\"text-align: left;\">1.- CORTAR EL BIZCOCHUELO EN TRES PARTES.</p>\r\n<p style=\"text-align: left;\">2.- HUMEDECER CADA PARTE CON EL SIROPE.</p>\r\n<p style=\"text-align: left;\">3.- COLOCAR LA CREMA DE GUAN&Aacute;BANA EN CADA BIZCOCHUELO E IR FORMANDO CAPAS.</p>\r\n<p style=\"text-align: left;\">4.- FORRAR LA TORTA CON LA CREMA CHANTILLY&nbsp; Y DECORAR CON CEREZAS.</p>',1,'2',0,NULL,'2023-12-12 16:10:46','2023-12-12 16:10:46'),
(39,'TORTA DE CHOCOLATE CON MANí','202312121348TORTA DE CHOCOLATE CON MANI.jpg','<p style=\"text-align: center;\"><strong><span style=\"text-decoration: underline;\">MONTAJE</span></strong></p>\r\n<p style=\"text-align: left;\">1.- ENMANTECAR Y ENHARINAR UN MOLDE DE 20 CM DE DIAMETRO.</p>\r\n<p style=\"text-align: left;\">2.- VOLCAR SOBRE EL MOLDE PREPARADO LA MITAD DE LA MEZCLA DE LA TORTA PARTE II.</p>\r\n<p style=\"text-align: left;\">3.- AGREGAR LA CREMA DE MAN&Iacute;.</p>\r\n<p style=\"text-align: left;\">4.- VOLCAR SOBRE LA CREMA DE MAN&Iacute; EL RESTANTE DEL BATIDO DE LA TORTA PARTE II.</p>\r\n<p style=\"text-align: left;\">5.- HORNEAR A 180 &deg; C&nbsp; ENTRE 50 A 60 MINUTOS.</p>\r\n<p style=\"text-align: left;\">6.- RETIRAR Y COLOCAR EN REJILLA HASTA QUE ESTE A TEMPERATURA AMBIENTE.</p>\r\n<p style=\"text-align: left;\">7.- VERTER EL GLASEADO SOBRE LA TORTA.</p>',1,'2',0,NULL,'2023-12-12 17:48:28','2023-12-12 17:48:28'),
(40,'BRIOCHE CON CUBIERTA DE CHOCOLATE','202312121434BRIOCHE CUBIERTA DE CHOCOLATE.jpg','<p style=\"text-align: center;\"><strong><span style=\"text-decoration: underline;\">MONTAJE</span></strong></p>\r\n<p style=\"text-align: left;\">1.- BA&Ntilde;AR HASTA LA MITAD EL BRIOCHE CON EL GLASEADO.</p>',1,'2',0,NULL,'2023-12-12 18:34:41','2023-12-12 18:34:41'),
(41,'TORTA OREO','202312121523TORTA OREO.jpg','<p style=\"text-align: center;\"><strong><span style=\"text-decoration: underline;\">MONTAJE</span></strong></p>\r\n<p style=\"text-align: left;\">1.- CORTAR EL BIZCOCHO JOCONDA EN CUATRO DISCOS.</p>\r\n<p style=\"text-align: left;\">2.- COLOCAR EL PRIMER DISCO SOBRE LA BANDEJA E HIDRATAR CON EL ALM&Iacute;BAR.</p>\r\n<p style=\"text-align: left;\">3.- COLOCAR UNA CAPA DE CREMA DE MANTEQUILLA AL ESTILO INGLES.</p>\r\n<p style=\"text-align: left;\">4.- COLOCAR EL SEGUNDO DISCO E HIDRATAR CON EL ALM&Iacute;BAR.</p>\r\n<p style=\"text-align: left;\">5.- COLOCAR UNA CAPA DE CREMA DE MANTEQUILLA AL ESTILO INGLES. REPETIR EL PROCESO.</p>\r\n<p style=\"text-align: left;\">6.- FRIZAR LA TORTA COMPLETA CON LA CREMA DE MANTEQUILLA Y LLEVAR AL CONGELADOR.</p>\r\n<p style=\"text-align: left;\">7.- RETIRAR DEL CONGELADOR Y NAPEAR CON LA GANACHE DE CHOCOLATE CON LECHE.</p>',1,'2',0,NULL,'2023-12-12 19:23:00','2023-12-12 19:23:00'),
(42,'COFFECAKE DE CAFÉ CON ALMENDRAS','202312141218COFFE CAKE CON ALMENDRAS.jpg','<p style=\"text-align: center;\"><strong><span style=\"text-decoration: underline;\">MONTAJE</span></strong></p>\r\n<p style=\"text-align: left;\">1.- PREPARAR UN MOLDE DE 20 CM DE DIAMETRO CON PAPEL MANTECA EN LA BASE.</p>\r\n<p style=\"text-align: left;\">2.- ENMANTECAR Y ENHARINAR EL MOLDE.</p>\r\n<p style=\"text-align: left;\">3.- VOLCAR SOBRE EL MOLDE PREPARADO LA MEZCLA DE BIZCOCHO Y ALISAR LA SUPERFICIE.</p>\r\n<p style=\"text-align: left;\">4.- ESPARCIR LA CUBIERTA SOBRE LA SUPERFICIE DEL BIZCOCHO.</p>\r\n<p style=\"text-align: left;\">5.- HORNEAR A 180 &deg; C POR 30 MINUTOS APROXIMADAMENTE.</p>\r\n<p style=\"text-align: left;\">6.- RETIRAR Y COLOCAR EN REJILLA.</p>\r\n<p style=\"text-align: left;\"><strong><span style=\"text-decoration: underline;\">A SABER...</span></strong></p>\r\n<p style=\"text-align: left;\">A.- CUANDO RETIRAMOS DEL HORNO LA PREPARACI&Oacute;N HAY QUE DEJAR REPOSAR ENTRE 5 A 10 MINUTOS. PASADO ESE TIEMPO PROCEDEMOS A DESMOLDAR NUESTRO PRODUCTO COLOCANDO EN REJILLA.</p>',1,'2',0,NULL,'2023-12-14 16:18:51','2023-12-14 16:18:51'),
(43,'COFFE CAKE CRUJIENTE DE CANELA','202312141253COFFE CAKE CRUJIENTE DE CANELA.jpg','<p style=\"text-align: center;\"><strong><span style=\"text-decoration: underline;\">MONTAJE</span></strong></p>\r\n<p style=\"text-align: left;\">1.- PREPARAR UN MOLDE DE 20 CM DE DIAMETRO, EN LA BASE CON PAPEL MANTECA.</p>\r\n<p style=\"text-align: left;\">2.- ENMATECAR Y ENHARINAR EL MOLDE.</p>\r\n<p style=\"text-align: left;\">3.- VOLCAR SOBRE EL MOLDE LA MITAD DE LA MEZCLA DEL BIZCOCHO.</p>\r\n<p style=\"text-align: left;\">4.- ESPARCIR EL RELLENO PRECIONANDO UN POCO PARA QUE SE ADHIERA.</p>\r\n<p style=\"text-align: left;\">5.- VOLCAMOS EN RESTO DE LA PREPARACI&Oacute;N DEL BIZCOCHO.</p>\r\n<p style=\"text-align: left;\">6.- ALISAMOS Y ESPARCIMOS EL TOPPING.</p>\r\n<p style=\"text-align: left;\">7.- HORNEAR A 160 &deg; C POR 45 MINUTOS APROXIMADAMENTE.</p>\r\n<p style=\"text-align: left;\">8.- RETIRAR Y COLOCAR EN REJILLA.</p>',1,'2',0,NULL,'2023-12-14 16:53:44','2023-12-14 16:53:44'),
(44,'RED VELVET CAKE','202312141800TORTA RED VELVET.jpg','<p style=\"text-align: center;\"><strong><span style=\"text-decoration: underline;\">MONTAJE</span></strong></p>\r\n<p style=\"text-align: left;\">1.- CORTAR EL BIZCOCHO EN TRES DISCOS.</p>\r\n<p style=\"text-align: left;\">2.- SEPARAR EL RELLENO&nbsp; EN TRES PARTES.</p>\r\n<p style=\"text-align: left;\">3.- DISPONER UN DISCO BIZCOCHO SOBRE BANDEJA&nbsp; Y VERTER UNA PARTE DE RELLENO.&nbsp;</p>\r\n<p style=\"text-align: left;\">4.- CUBRIR CON EL SEGUNDO DISCO DE BIZCOCHO Y VERTER OTRA PARTE DEL RELLENO.</p>\r\n<p style=\"text-align: left;\">5.- COLOCAR SOBRE EL RELLENO EL ULTIMO DISCO DE BIZCOCHO.</p>\r\n<p style=\"text-align: left;\">6.- PARA TERMINAR CUBRIR LA TORTA CON UNA CAPA DE RELLENO. RESERVAR EN LA NEVERA.</p>\r\n<p style=\"text-align: left;\">7.- DECORAR CON RIZOS DE CREMA DEL RELLENO Y VIRUTAS DE CHOCOLATE.</p>\r\n<p style=\"text-align: left;\">&nbsp;</p>',1,'2',0,NULL,'2023-12-14 22:00:29','2023-12-14 22:00:29'),
(45,'LE CONCORDE','202312141911LE CONCORDE.jfif','<p style=\"text-align: center;\"><strong><span style=\"text-decoration: underline;\">MONTAJE</span></strong></p>\r\n<p style=\"text-align: left;\">1.- COLOCAR UNA CINTA DE ACETATO ALREDEDOR DE UN ARO DE 20 CM DE DIAMETRO.</p>\r\n<p style=\"text-align: left;\">2.- COLOCAR UN DISCO DE MERENGUE EN EL FONDO DEL ARO.</p>\r\n<p style=\"text-align: left;\">3.- RELLENAR EN FORMA UNIFORME CON LA MEZCLA DE LA MOUSSE HASTA LA MITAD DE LA ALTURA DEL ARO.</p>\r\n<p style=\"text-align: left;\">4.- COLOCAR OTRO DISCO DE MERENGUE.</p>\r\n<p style=\"text-align: left;\">5.- CUBRIR CON LA MOUSSE Y ENFRIAR.</p>\r\n<p style=\"text-align: left;\">6.- QUITAR CON CUIDADO EL ARO Y LA CINTA DE ACETATO.</p>\r\n<p style=\"text-align: left;\">7.- DECORAR CON LOS BASTONCITOS DE MERENGUE.</p>\r\n<p style=\"text-align: left;\">8.- ESPOLVOREAR CON AZ&Uacute;CAR GLAS.</p>\r\n<p style=\"text-align: left;\">&nbsp;</p>',1,'2',0,NULL,'2023-12-14 23:11:12','2023-12-14 23:11:12'),
